/*
import React from 'react';
import { Text, StyleSheet, View, TouchableOpacity, TextInput } from 'react-native';
import { useState } from "react";
import {PrimaryButton} from '../components/Button';

const LoginScreen = ({ navigation }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [username, setUsername] = useState("");
  return (
    <View style={styles.bg}>
      <Text style={styles.text}>Near me</Text >

      <View style={[styles.background, {marginBottom:20} ]}>
        <Text style={styles.iconstyle}>Username</Text>
        <TextInput style={styles.inputstyle}
          placeholder="Username."
          placeholderTextColor="#003f5c"
          onChangeText={(username) => setUsername(username)}/>
      </View>

      <View style={styles.background}>
        <Text style={styles.iconstyle}>Email</Text>
        <TextInput style={styles.inputstyle}
          placeholder="Email."
          placeholderTextColor="#003f5c"
          onChangeText={(email) => setEmail(email)}/>
      </View>

      <TouchableOpacity onPress={() => navigation.navigate('Sign')}>
        <Text style={styles.asktext}>Don't have an account? sign in with us here!</Text>
      </TouchableOpacity>

      <View style={styles.background}>
        <Text style={styles.iconstyle}>Password</Text>
        <TextInput style={styles.inputstyle}
          placeholder="Password."
          placeholderTextColor="#003f5c"
          secureTextEntry={true}
          onChangeText={(password) => setPassword(password)}/>
      </View>

      <TouchableOpacity>
        <Text style={styles.asktext}>Forgot password</Text>
      </TouchableOpacity>

      <PrimaryButton
        //onPress={() => {navigation.navigate('Canteen'); console.log(password)}}
        onPress={() => {
          if (email.trim() === "" || password.trim() === "" || username.trim() === "") {
          } 
          else {
            {navigation.navigate('Canteen'); console.log(email)};
          }
        }}
        title="Login"
      />
    </View>
  );
};

const styles = StyleSheet.create({
  text: {
    fontSize: 30,
    fontWeight: 'bold',
    paddingBottom: 20,
    paddingTop: 20,
    alignSelf: 'center'
  },
  input: {
      margin: 15,
      borderColor: 'black',
      borderWidth: 1
  },
  bg: {
    backgroundColor: 'white',
    flexDirection: 'column'
  },
  asktext: {
    fontSize: 10,
    color: 'orange',
    paddingBottom: 20,
    paddingLeft: 20
  },
  background: {
    backgroundColor: '#F0EEEE',
    borderRadius: 10,
    marginHorizontal: 15,
    height: 50,
    flexDirection: 'column',
    marginTop: 10
  },
  inputstyle: {
    fontSize: 14,
    paddingLeft: 20
  },
  iconstyle:{
    fontSize: 14,
    marginHorizontal: 10,
    fontWeight: 'bold'
  }
});

export default LoginScreen;
*/
