import React from 'react';
import { ScrollView, Text, StyleSheet, TouchableOpacity } from 'react-native';
import MenuDetail from '../components/MenuDetail';

const MenuScreen = ({navigation}) => {
    return (
    <ScrollView>
        <Text style={styles.category}>Main Dish</Text>

        <TouchableOpacity>
        <MenuDetail 
        title='Fried Rice' 
        imageSource = {require('../../assets/menu/FriedRice.jpg')}
        price='45 baht'/>
        </TouchableOpacity>

        <MenuDetail title='Pad thai'
        imageSource = {require('../../assets/menu/Rice.jpg')}
        price='40 baht'/>

        
        <MenuDetail title=' Chicken'
        imageSource = {require('../../assets/menu/FriedChick.jpeg')}
        price='35 baht'/>

        <Text style={styles.category}>Appetizer</Text>

        <MenuDetail title='Fried chicken'
        imageSource = {require('../../assets/menu/FriedChick.jpeg')}
        price='35 baht'/>

        <MenuDetail title='French fries'
        imageSource = {require('../../assets/menu/FriedChick.jpeg')}
        price='35 baht'/>

        <Text style={styles.category}>Drinks</Text>

        <MenuDetail title='Coke'
        imageSource = {require('../../assets/menu/FriedChick.jpeg')}
        price='18 baht'/>

        </ScrollView>   
    )
}

const styles = StyleSheet.create({
    category:
    {
        left: 15,
        fontSize: 20,
        fontWeight: "bold"
    }

  });
  
  export default MenuScreen;
