import React from 'react';
import { Text, StyleSheet, ScrollView, TouchableOpacity, FlatList, Image } from 'react-native';
import Searchbox from '../components/Searchbox';
import ShopDetail from '../components/ShopDetail';


const FavouriteScreen = ({route, navigation}) => {

  const shops = [
    { name: 'Shop1', shopimg: require('../../assets/menu/FriedRice.jpg'), description:'thai food', goto: 'Menu', canteenloc:'phrathep' }
  ];

  return (
    <>
    <Searchbox/>
        <Text style = {styles.Header}>Your favourite shops</Text>
        <FlatList
          keyExtractor={shop => shop.name}
          data={shops}
          renderItem={({ item }) => {
            return (
              <TouchableOpacity onPress={() => navigation.navigate(item.goto)}>
              <ShopDetail shop={item}/>
              </TouchableOpacity>
            );
          }}
        />
    </>
  );
};

const styles = StyleSheet.create({
  Header: {
    fontSize: 25,
    fontWeight: 'bold',
    left: 15,
    top: 10,
    paddingBottom: 15
  },
});

export default FavouriteScreen;
