import React from 'react';
import { Text, StyleSheet, ScrollView, TouchableOpacity, FlatList, Image, View, Dimensions,TouchableHighlight, TextInput } from 'react-native';
import Searchbox from '../components/Searchbox';
import { useState, useEffect } from "react";
import Icon from 'react-native-vector-icons/MaterialIcons';
import COLORS from '../consts/colors';


const {width} = Dimensions.get('screen');   //get size of current screen to calculate card width 
const cardWidth = width - 20;               //card width constant when we have 2 cards per row



const ShopScreen = ({ route, navigation}) => {

  //const { canteenid } = route.params;
  
  const apiUrl = window.apiurl + 'api/shops/shops/market/1/';

  const [Shops, setShops] = useState([]);
  const [Canteens, setCanteens] = useState([]);

  useEffect(() => {
    ShopsloadData();
    CanteenloadData();
  }, []);


 const ShopsloadData = async () => {
    const response = await fetch(apiUrl);
    const data = await response.json();
    setShops(data);
    console.log(data);
  }

  const CanteenloadData = async () => {
    const response = await fetch(window.apiurl + 'api/markets/markets/3/');
    const data = await response.json();
    setCanteens(data);
    console.log(data);
  }

  const Card = ({stores}) => {
    return (
      <TouchableHighlight
        underlayColor={COLORS.white}
        activeOpacity={0.9}
        onPress={() => navigation.navigate('SelectMenuScreen')}>
        <View style={style.card}>
          <View style={{alignItems: 'flex-end', top: 20, left:-20 }}>
            <Image source={stores.main_image} style={{height: 120, width: 120, borderRadius: 15}} />
          </View>
          <View style={{marginHorizontal: 20, marginVertical: -80}}>
            <Text style={{fontSize: 18, fontWeight: 'bold'}}>{stores.name}</Text>
            <Text style={{fontSize: 16, color: COLORS.grey, marginTop: 2}}>
              {stores.category}
            </Text>
            <View style={{flexDirection: 'row'}}>
              <View style={style.addToFavoriteBtn}>
                  <Icon name="star" size={20} color={COLORS.white} />
              </View>
            </View>
          </View>
        </View>
      </TouchableHighlight>
    );
  };

/*
  const shops = [
    { name: 'Shop1', shopimg: require('../../assets/menu/FriedRice.jpg'), description:'thai food', goto: 'Menu', canteenloc:'phrathep' },
    { name: 'Shop2', shopimg: require('../../assets/menu/FriedRice.jpg'), description:'international food', goto: 'Menu', canteenloc:'phrathep' },
    { name: 'Shop3', shopimg: require('../../assets/menu/FriedRice.jpg'), description:'Noodle ', goto: 'Menu', canteenloc:'phrathep'}
  ];
*/

  return (
    <>
    <Searchbox/>
    <ScrollView>
        <Text style = {styles.Header}>ร้านค้าในบริเวณ{Canteens.name}</Text> 
        <FlatList
          data={Shops}
          renderItem={({ item }) => {
            return (
              <TouchableOpacity onPress={() => navigation.navigate('Menu')}>
              <Card stores={item}/>
              </TouchableOpacity>
            );
          }}
        />
    </ScrollView>  
    </>
  );
};

const styles = StyleSheet.create({
  header: {
    marginTop: 20,
    flexDirection: 'row',
    justifyContent: 'space-between',
    paddingHorizontal: 20,
  },
  card: {
    height: 160,
    width: cardWidth,
    marginHorizontal: 10,
    marginBottom: 10,
    marginTop: 10,
    borderRadius: 15,
    elevation: 13,
    backgroundColor: COLORS.white,
  },
  inputContainer: {
    flex: 1,
    height: 50,
    borderRadius: 10,
    flexDirection: 'row',
    backgroundColor: COLORS.light,
    alignItems: 'center',
    paddingHorizontal: 20,
  },
  searchBtn: {
    width: 50,
    height: 50,
    marginLeft: 10,
    backgroundColor: COLORS.primary,
    borderRadius: 10,
    justifyContent: 'center',
    alignItems: 'center',
  },
  addToFavoriteBtn: {
    height: 35,
    width: 35,
    borderRadius: 20,
    backgroundColor: COLORS.primary,
    justifyContent: 'center',
    alignItems: 'center',
    marginLeft: 125,
    marginTop: 20,
  },
});

export default ShopScreen;
