const handleResponse = res => {
  if(res.ok) {
    return res.json()
  }
  throw new Error('Network response was not ok.')
}

const apicall = () => {

  const apiUrl = 'https://nearme-kmitl.herokuapp.com/api/accounts/customers/';
  const csrfToken = getCookie('CSRF-TOKEN');
  console.log(csrfCookie)


  fetch(apiUrl, {
    //mode: 'no-cors', 
    method: 'post',
    credentials: 'include',
    headers: {
      'Accept': 'application/json',
      'Content-Type': 'application/json',
      'X-CSRFTOKEN': csrfCookie,
    },
    body: JSON.stringify(
      {
        username: 12,
      }
    )
  }).then(handleResponse)
  .then(data => console.log(data.key)) 
  .catch(error => console.log("Error detected: " + error))
}

const SignupScreen = ({ navigation }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [fname, setFname] = useState("");
  const [lname, setLname] = useState("");
  const [cpassword, setCpassword] = useState("");
  const [username, setUsername] = useState("");

  React.useEffect(() => {
    console.log("Hello");
  }, []);

  return (
    <View style={styles.bg}>
      <Text style={styles.text}>Near me</Text >

      <View style={[styles.background]}>
        <Text style={styles.iconstyle}>Email</Text>
        <TextInput style={styles.inputstyle}
          placeholder="Email."
          placeholderTextColor="#003f5c"
          onChangeText={(email) => setEmail(email)}/>
      </View>

      <TouchableOpacity onPress={() => navigation.navigate('Login')}>
        <Text style={styles.asktext}>Have an account already, login here</Text>
      </TouchableOpacity>

      <View style={[styles.background]}>
        <Text style={styles.iconstyle}>First name</Text>
        <TextInput style={styles.inputstyle}
          placeholder="John"
          placeholderTextColor="#003f5c"
          onChangeText={(fname) => setFname(fname)}/>
      </View>

      <View style={[styles.background, {marginBottom:20} ]}>
        <Text style={styles.iconstyle}>Last name</Text>
        <TextInput style={styles.inputstyle}
          placeholder="Doe"
          placeholderTextColor="#003f5c"
          onChangeText={(lname) => setLname(lname)}/>
      </View>

      <View style={[styles.background, {marginBottom:20} ]}>
        <Text style={styles.iconstyle}>Username</Text>
        <TextInput style={styles.inputstyle}
          placeholder="Username."
          placeholderTextColor="#003f5c"
          onChangeText={(username) => setUsername(username)}/>
      </View>

      <View style={[styles.background]}>
        <Text style={styles.iconstyle}>Password</Text>
        <TextInput style={styles.inputstyle}
          placeholder="Password."
          placeholderTextColor="#003f5c"
          onChangeText={(password) => setPassword(password)}/>
      </View>

      <View style={[styles.background, {marginBottom:20} ]}>
        <Text style={styles.iconstyle}>Confirm Password</Text>
        <TextInput style={styles.inputstyle}
          placeholder="Retype password."
          placeholderTextColor="#003f5c"
          onChangeText={(cpassword) => setCpassword(cpassword)}/>
      </View>

      <PrimaryButton
        //onPress={() => navigation.navigate('Home')}
        onPress={() => {apicall()}}
        title="Signup"
      />
    </View>
    
  );
};

const styles = StyleSheet.create({
  text: {
    fontSize: 30,
    fontWeight: 'bold',
    paddingBottom: 20,
    paddingTop: 20,
    alignSelf: 'center'
  },
  input: {
      margin: 15,
      borderColor: 'black',
      borderWidth: 1
  },
  bg: {
    backgroundColor: 'white',
    flexDirection: 'column'
  },
  asktext: {
    fontSize: 10,
    color: 'orange',
    paddingBottom: 20,
    paddingLeft: 20
  },
  background: {
    backgroundColor: '#F0EEEE',
    borderRadius: 10,
    marginHorizontal: 15,
    height: 50,
    flexDirection: 'column',
    marginTop: 10
  },
  inputstyle: {
    fontSize: 14,
    paddingLeft: 20
  },
  iconstyle:{
    fontSize: 14,
    marginHorizontal: 10,
    fontWeight: 'bold'
  }
});


export default SignupScreen;



