  data.append("username", "ABCD");
  data.append("email", "1234");
  data.append("first_name", "ABCD");
  data.append("last_name", "1234");
  data.append("password", "ABCD");
  data.append("phone", "1234");
  data.append("type", "ABCD");

var user = new FormData();
  user.append("user", data)
*/

const SignupScreen = ({ navigation }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [fname, setFname] = useState("");
  const [lname, setLname] = useState("");
  const [cpassword, setCpassword] = useState("");
  const [username, setUsername] = useState("");
  return (
    <View style={styles.bg}>
      <Text style={styles.text}>Near me</Text >
      
      <View style={[styles.background, {marginBottom:20} ]}>
        <Text style={styles.iconstyle}>Username</Text>
        <TextInput style={styles.inputstyle}
          placeholder="Username."
          placeholderTextColor="#003f5c"
          onChangeText={(username) => setUsername(username)}/>
      </View>
      <TouchableOpacity onPress={() => navigation.navigate('Login')}>
        <Text style={styles.asktext}>Have an account already, login here</Text>
      </TouchableOpacity>

      <InputBox title = 'First name'/>
      <InputBox title = 'Last name'/>
      <InputBox title = 'Password'/>
      <InputBox title = 'Confrim Password'/>

      <PrimaryButton
        onPress={() => navigation.navigate('Home')}
        title="Signup"
      />
    </View>
    
  );
};

const styles = StyleSheet.create({
  text: {
    fontSize: 30,
    fontWeight: 'bold',
    paddingBottom: 20,
    paddingTop: 20,
    alignSelf: 'center'
  },
  input: {
      margin: 15,
      borderColor: 'black',
      borderWidth: 1
  },
  bg: {
    backgroundColor: 'white',
    flexDirection: 'column'
  },
  asktext: {
    fontSize: 10,
    color: 'orange',
    paddingLeft: 20
  },
  background: {
    backgroundColor: '#F0EEEE',
    borderRadius: 10,
    marginHorizontal: 15,
    height: 50,
    flexDirection: 'row',
    marginTop: 10
},
inputstyle: {
    flex: 1,
    fontSize: 14
},
iconstyle:{
    fontSize: 30,
    alignSelf: 'center',
    marginHorizontal: 10
}
});

export default SignupScreen;


