import React from 'react';
import { useState } from "react";
import { Text, StyleSheet, View, Button, TouchableOpacity, TextInput} from 'react-native';
import InputBox from '../components/InputBox';
import {PrimaryButton} from '../components/Button';

const LoginScreen = ({ navigation }) => {

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  return (
    <View style={styles.bg}>
      <Text style={styles.text}>Near me</Text >
      <InputBox title = 'Email' onChangeText={(email) => setEmail(email)}/>
      <TouchableOpacity onPress={() => navigation.navigate('Sign')}>
        <Text style={styles.asktext}>Don't have an account? sign in with us here!</Text>
      </TouchableOpacity>

      <InputBox title = 'Password' 
      onChangeText={(password) => setPassword(password)}/>
      <TouchableOpacity>
        <Text style={styles.asktext}>Forgot password</Text>
      </TouchableOpacity>

      <PrimaryButton
        onPress={() => navigation.navigate('Canteen')}
        title="Login"
      />
    </View>
  );
};

const styles = StyleSheet.create({
  text: {
    fontSize: 30,
    fontWeight: 'bold',
    paddingBottom: 20,
    paddingTop: 20,
    alignSelf: 'center'
  },
  input: {
      margin: 15,
      borderColor: 'black',
      borderWidth: 1
  },
  bg: {
    backgroundColor: 'white',
    flexDirection: 'column'
  },
  asktext: {
    fontSize: 10,
    color: 'orange',
    paddingBottom: 20,
    paddingLeft: 20
  },
});

export default LoginScreen;
