import React from 'react';
import { Text, StyleSheet, View, Button, TouchableOpacity, TextInput } from 'react-native';
import InputBox from '../components/InputBox';

const SignupScreen = ({ navigation }) => {
  return (
    <View style={styles.bg}>
      <Text style={styles.text}>Near me</Text >
      <InputBox title = 'Email'/>
      <TouchableOpacity onPress={() => navigation.navigate('Login')}>
        <Text style={styles.asktext}>Have an account already, login here</Text>
      </TouchableOpacity>

      <InputBox title = 'First name'/>
      <InputBox title = 'Last name'/>
      <InputBox title = 'Password'/>
      <InputBox title = 'Confrim Password'/>

      <Button
        onPress={() => navigation.navigate('Canteen')}
        title="Signup"
      />
    </View>
  );
};

const styles = StyleSheet.create({
  text: {
    fontSize: 30,
    fontWeight: 'bold',
    paddingBottom: 20,
    paddingTop: 20,
    alignSelf: 'center'
  },
  input: {
      margin: 15,
      borderColor: 'black',
      borderWidth: 1
  },
  bg: {
    backgroundColor: 'white',
    flexDirection: 'column'
  },
  asktext: {
    fontSize: 10,
    color: 'orange',
    paddingLeft: 20
  },
});

export default SignupScreen;
