import React from 'react';
import { Text, StyleSheet, ScrollView, TouchableOpacity, FlatList, Image } from 'react-native';
import Searchbox from '../components/Searchbox';
import ShopDetail from '../components/ShopDetail';
import { useRoute,useNavigation } from '@react-navigation/native';
const route = useRoute();


const CanteenScreen = ({navigation}) => {
  console.log(route.params.canteenid)

  const shops = [
    { name: 'Shop1', shopimg: require('../../assets/menu/FriedRice.jpg'), description:'thai food', goto: 'Menu', canteenloc:'phrathep' },
    { name: 'Shop2', shopimg: require('../../assets/menu/FriedRice.jpg'), description:'international food', goto: 'Menu', canteenloc:'phrathep' },
    { name: 'Shop3', shopimg: require('../../assets/menu/FriedRice.jpg'), description:'Noodle ', goto: 'Menu', canteenloc:'phrathep'}
  ];

  return (
    <>
    <Searchbox/>
    <ScrollView>
        <Text style = {styles.Header}>Shops in Phrathep</Text>
        <FlatList
          keyExtractor={shop => shop.name}
          data={shops}
          renderItem={({ item }) => {
            return (
              <TouchableOpacity onPress={() => navigation.navigate(item.goto)}>
              <ShopDetail shop={item}/>
              </TouchableOpacity>
            );
          }}
        />
    </ScrollView>  
    </>
  );
};

const styles = StyleSheet.create({
  Header: {
    fontSize: 25,
    fontWeight: 'bold',
    left: 15,
    top: 10,
    paddingBottom: 15
  },
});

export default CanteenScreen;
