import React from 'react';
import { Text, StyleSheet, ScrollView, TouchableOpacity, FlatList, Image } from 'react-native';
import Searchbox from '../components/Searchbox';
import ShopDetail from '../components/ShopDetail';
import { useState, useEffect } from "react";


const CanteenScreen = ({route, navigation}) => {

  const { id } = route.params;

  const apiUrl = window.apiurl + 'api/shops/shops/market/1/';

  const [Shops, setShops] = useState([]);

  useEffect(() => {
    loadData();
  }, []);


 const loadData = async () => {
    const response = await fetch(apiUrl);
    const data = await response.json();
    setShops(data);
    console.log(data);
  }

  const shops = [
    { name: 'Shop1', shopimg: require('../../assets/menu/FriedRice.jpg'), description:'thai food', goto: 'Menu', canteenloc:'phrathep' },
    { name: 'Shop2', shopimg: require('../../assets/menu/FriedRice.jpg'), description:'international food', goto: 'Menu', canteenloc:'phrathep' },
    { name: 'Shop3', shopimg: require('../../assets/menu/FriedRice.jpg'), description:'Noodle ', goto: 'Menu', canteenloc:'phrathep'}
  ];

  return (
    <>
    <Searchbox/>
    <ScrollView>
        <Text style = {styles.Header}>Shops in Phrathep</Text>
        <FlatList
          keyExtractor={shop => shop.name}
          data={shops}
          renderItem={({ item }) => {
            return (
              <TouchableOpacity onPress={() => console.log()}>
              <ShopDetail shop={item}/>
              </TouchableOpacity>
            );
          }}
        />
    </ScrollView>  
    </>
  );
};

const styles = StyleSheet.create({
  Header: {
    fontSize: 25,
    fontWeight: 'bold',
    left: 15,
    top: 10,
    paddingBottom: 15
  },
});

export default CanteenScreen;
