import React from 'react';
import { SafeAreaView, Text, StyleSheet, View, Button, TouchableOpacity, TextInput, Image } from 'react-native';
import { useState } from "react";
import InputBox from '../components/InputBox';
import {PrimaryButton} from '../components/Button';
import Icon from 'react-native-vector-icons/MaterialIcons';
import COLORS from '../consts/colors';
import STYLES from '../styles/styles';
import {ScrollView} from 'react-native-gesture-handler';



const ProfileScreen = ({ navigation }) => {

  const handleResponse = res => {
    if(res.ok) {
      return res.json()
    }
    throw new Error('Network response was not ok.')
  }

  const apicall = () => {

    const apiUrl = window.apiurl + 'api/accounts/customers/user/12/' ;

    fetch(apiUrl, {
      //mode: 'no-cors',
      method: 'get',
      credentials: 'include',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      },})
      .then(handleResponse)
      .then(data => console.log(data))
      .catch(error => console.log("Error detected: " + error)) 
  }

  return (
    <SafeAreaView
      style={{paddingHorizontal: 20, flex: 1, backgroundColor: COLORS.white}}>
      <ScrollView showsVerticalScrollIndicator={false}>
        <View style={{flexDirection: 'row', marginTop: 40, }}>
          <Text style={{fontWeight: 'bold', fontSize: 22, color: COLORS.dark}}>
            Near
          </Text>
          <Text
            style={{fontWeight: 'bold', fontSize: 22, color: COLORS.primary}}>
            Me
          </Text>
        </View>

        <View style={{marginTop: 40}}>
          <Text style={{fontSize: 27, fontWeight: 'bold', color: COLORS.dark}}>
            บัญชีผู้ใช้
          </Text>
        </View>

        <View style={{marginTop: 20}}>
          <View style={STYLES.inputContainer}>
              <Text style={STYLES.input}>ชื่อบัญชี: </Text> 
          </View>
          <View style={STYLES.inputContainer}>
              <Text style={STYLES.input}>เบอร์โทรศัพท์:</Text>
          </View>
          <View style={STYLES.inputContainer}>
              <Text style={STYLES.input}>คำอธิบายร้าน:</Text>
          </View>
          <View style={STYLES.inputContainer}>
              <Text style={STYLES.input}>ที่ตั้งร้าน: </Text>
          </View>
          
          <View style={{marginHorizontal: 30 , marginVertical:50}}>
          <TouchableOpacity onPress={() => navigation.navigate('Home')}>
              <PrimaryButton title="แก้ไขข้อมูลร้าน"/>
          </TouchableOpacity>
          <Text></Text>
          <TouchableOpacity onPress={() => navigation.navigate('Home')}>
              <PrimaryButton title="เปลี่ยนรูปประจำร้าน"/>
          </TouchableOpacity>
          </View>
          <View
            style={{
              marginVertical: 20,
              flexDirection: 'row',
              justifyContent: 'center',
              alignItems: 'center',
            }}>
          </View>
          <View
            style={{
              flexDirection: 'row',
              justifyContent: 'space-between',
            }}>
            
          </View>
        </View>

        <View
          style={{
            flexDirection: 'row',
            alignItems: 'flex-end',
            justifyContent: 'center',
            marginTop: 40,
            marginBottom: 20,
          }}>
        </View>
      </ScrollView>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  text: {
    fontSize: 30,
    fontWeight: 'bold',
    paddingBottom: 20,
    paddingTop: 20,
    alignSelf: 'center'
  },
  input: {
      margin: 15,
      borderColor: 'black',
      borderWidth: 1
  },
  bg: {
    backgroundColor: 'white',
    flexDirection: 'column'
  },
  asktext: {
    fontSize: 10,
    color: 'orange',
    paddingBottom: 20,
    paddingLeft: 20
  },
});

export default ProfileScreen;



