import React from 'react';
import { Text, StyleSheet, View, TouchableOpacity, TextInput } from 'react-native';
import { useState } from "react";
import {PrimaryButton} from '../components/Button';
import { useNavigation } from "@react-navigation/native";

const LoginScreen = ({ navigation }) => {
  const [password, setPassword] = useState("");
  const [username, setUsername] = useState("");

  handleResponse = res => {
    if(res.ok) {
      return res.json()
    }
    throw new Error('Network response was not ok.')
  }


  apicall = (password, username) => {

    const apiUrl = 'https://nearme-kmitl.herokuapp.com/api/dj-rest-auth/login/';

    fetch(apiUrl, {
      //mode: 'no-cors', 
      method: 'post',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(
        {
          username: username,
          password: password,
        }
      )
    }).then(handleResponse)
    .then(data => console.log(data))   
  }

  return (
    <View style={styles.bg}>
      <Text style={styles.text}>Near me</Text >

      <View style={[styles.background]}>
        <Text style={styles.iconstyle}>Username</Text>
        <TextInput style={styles.inputstyle}
          placeholder="Username."
          placeholderTextColor="#003f5c"
          onChangeText={(username) => setUsername(username)}/>
      </View>

      <TouchableOpacity onPress={() => navigation.navigate('Sign')}>
        <Text style={styles.asktext}>Don't have an account? sign in with us here!</Text>
      </TouchableOpacity>

      <View style={styles.background}>
        <Text style={styles.iconstyle}>Password</Text>
        <TextInput style={styles.inputstyle}
          placeholder="Password."
          placeholderTextColor="#003f5c"
          secureTextEntry={true}
          onChangeText={(password) => setPassword(password)}/>
      </View>

      <TouchableOpacity>
        <Text style={styles.asktext}>Forgot password</Text>
      </TouchableOpacity>

      <PrimaryButton
        onPress={() => {
          if (password.trim() === "" || username.trim() === "") {
          } 
          else {
            //{navigation.navigate('Canteen');};
            {apicall(password, username)};
          }
        }}
        title="Login"
      />
    </View>
  );
};

const styles = StyleSheet.create({
  text: {
    fontSize: 30,
    fontWeight: 'bold',
    paddingBottom: 20,
    paddingTop: 20,
    alignSelf: 'center'
  },
  input: {
      margin: 15,
      borderColor: 'black',
      borderWidth: 1
  },
  bg: {
    backgroundColor: 'white',
    flexDirection: 'column'
  },
  asktext: {
    fontSize: 10,
    color: 'orange',
    paddingBottom: 20,
    paddingLeft: 20
  },
  background: {
    backgroundColor: '#F0EEEE',
    borderRadius: 10,
    marginHorizontal: 15,
    height: 50,
    flexDirection: 'column',
    marginTop: 10
  },
  inputstyle: {
    fontSize: 14,
    paddingLeft: 20
  },
  iconstyle:{
    fontSize: 14,
    marginHorizontal: 10,
    fontWeight: 'bold'
  }
});

export default LoginScreen;





