import React from 'react';
import { Text, StyleSheet, ScrollView, TouchableOpacity, FlatList, Image, View } from 'react-native';
import Searchbox from '../components/Searchbox';
import { useState, useEffect } from "react";


const ShopScreen = ({ route, navigation}) => {

  //const { canteenid } = route.params;
  
  const apiUrl = window.apiurl + 'api/shops/shops/market/1/';

  const [Shops, setShops] = useState([]);
  const [Canteens, setCanteens] = useState([]);

  useEffect(() => {
    ShopsloadData();
    CanteenloadData();
  }, []);


 const ShopsloadData = async () => {
    const response = await fetch(apiUrl);
    const data = await response.json();
    setShops(data);
    console.log(data);
  }

  const CanteenloadData = async () => {
    const response = await fetch(window.apiurl + 'api/markets/markets/3/');
    const data = await response.json();
    setCanteens(data);
    console.log(data);
  }

  const Card = ({stores}) => {
    return (
      
      <TouchableHighlight
        underlayColor={COLORS.white}
        activeOpacity={0.9}
        onPress={() => navigation.navigate('SelectMenuScreen')}>
        <View style={style.card}>
          <View style={{alignItems: 'flex-end', top: 20, left:-20 }}>
            <Image source={stores.image} style={{height: 120, width: 120, borderRadius: 15}} />
          </View>
          <View style={{marginHorizontal: 20, marginVertical: -80}}>
            <Text style={{fontSize: 18, fontWeight: 'bold'}}>{stores.name}</Text>
            <Text style={{fontSize: 16, color: COLORS.grey, marginTop: 2}}>
              {stores.category}
            </Text>
            <View style={{flexDirection: 'row'}}>
              <Text style={{fontSize: 18, fontWeight: 'bold'}}>
                สถานะ :<Text>{' '}</Text>
              </Text>
              <Text style={{fontSize: 18, fontWeight: 'bold', color: COLORS.primary}}>
                {stores.status}
              </Text>
              <View style={style.addToFavoriteBtn}>
                  <Icon name="star" size={20} color={COLORS.white} />
              </View>
            </View>
          </View>
        </View>
      </TouchableHighlight>
    );
  };

  const ShopDetail = ({shop}) => {
    return (
      <View style={styles.background}>
        <img src={shop.main_image} width="100" height="100"/>
        <Text style={styles.titlestyle}>{shop.name}</Text>
      </View>
    )
  }

/*
  const shops = [
    { name: 'Shop1', shopimg: require('../../assets/menu/FriedRice.jpg'), description:'thai food', goto: 'Menu', canteenloc:'phrathep' },
    { name: 'Shop2', shopimg: require('../../assets/menu/FriedRice.jpg'), description:'international food', goto: 'Menu', canteenloc:'phrathep' },
    { name: 'Shop3', shopimg: require('../../assets/menu/FriedRice.jpg'), description:'Noodle ', goto: 'Menu', canteenloc:'phrathep'}
  ];
*/

  return (
    <>
    <Searchbox/>
    <ScrollView>
        <Text style = {styles.Header}>ร้านค้าในบริเวณ{Canteens.name}</Text> 
        <FlatList
          data={Shops}
          renderItem={({ item }) => {
            return (
              <TouchableOpacity onPress={() => navigation.navigate('Menu')}>
              <ShopDetail shop={item}/>
              </TouchableOpacity>
            );
          }}
        />
    </ScrollView>  
    </>
  );
};

const styles = StyleSheet.create({
  Header: {
    fontSize: 25,
    fontWeight: 'bold',
    left: 15,
    top: 10,
    paddingBottom: 15
  },
  titlestyle: {
    fontSize: 25,
    paddingLeft: 10,
    paddingTop: 10,
    flex: 1,
    fontWeight: "bold"

  },
  descriptionstyle: {
    fontSize: 15,
    left: 110,
    position: 'absolute',
    top:55
  },
  
  image: {
    width: 100,
    height: 100
},
background: {
  backgroundColor: '#EFEFEF',
  borderRadius: 5,
  marginHorizontal: 15,
  height: 100,
  margin: 10,
  flexDirection: 'row',
}
});

export default ShopScreen;
