import React from 'react';
import { View, Text, StyleSheet, TouchableOpacity, FlatList } from 'react-native';
import MenuDetail from '../components/MenuDetail';

const MenuScreen = ({navigation}) => {
    const menus = [
        { name: 'Fried Rice', menuimg: require('../../assets/menu/FriedRice.jpg'), price:'45'},
        { name: 'PadThai', menuimg: require('../../assets/menu/Rice.jpg'), price:'35'},
        { name: 'Tomyum', menuimg: require('../../assets/menu/FriedRice.jpg'), price:'45'},
        { name: 'Fried Rice', menuimg: require('../../assets/menu/FriedRice.jpg'), price:'45'},
        { name: 'Fried Rice', menuimg: require('../../assets/menu/FriedRice.jpg'), price:'45'},
        { name: 'Fried Rice', menuimg: require('../../assets/menu/FriedRice.jpg'), price:'45'},
      ];
      return (
        <View style={styles.bg}>
            <FlatList
              keyExtractor={menu => menu.name}
              data={menus}
              renderItem={({ item }) => {
                return (
                  <TouchableOpacity>
                  <MenuDetail menu={item}/>
                  </TouchableOpacity>
                );
              }}
            />
        </View>  
      );
}

const styles = StyleSheet.create({
    category:
    {
        left: 15,
        fontSize: 20,
        fontWeight: "bold"
    },
    bg: {
      backgroundColor: 'white'
    }

  });
  
  export default MenuScreen;
