import React from 'react';
import { Text, StyleSheet, View, TouchableOpacity, TextInput } from 'react-native';
import { useState } from "react";
import {PrimaryButton} from '../components/Button';

const apicall = (email, password, username) => {

  const apiUrl = 'https://nearme-kmitl.herokuapp.com/api/dj-rest-auth/login/';
  const [key, setKey] = useState("");
  const [user, setUser] = useState("");
  fetch(apiUrl, {
    //mode: 'no-cors', 
    method: 'post',
    withCredentials: true,
    headers: {
      'Accept': 'application/json',
      'Content-Type': 'application/json',
    },
    body: JSON.stringify(
      {
        username: username,
        email: email,
        password: password,
      }
    )
  }).then(response => response.text())
  .then(data => console.log(data))
  .then((data) => {
    ({ contacts: data })
  })    
  .catch(error => console.log("Error detected: " + error))
  
  
}

const LoginScreen = ({ navigation }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [username, setUsername] = useState("");

  /*
  React.useEffect(() => {
    fetch(apiUrl, {
      //mode: 'no-cors', 
      method: 'post',
      withCredentials: true,
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(
        {
          username: "shop01",
          email: "shop01@example.com",
          password: "pp123456789",
        }
      )
    }).then(response => response.text())
    .then(data => console.log(data))    
    .catch(error => console.log("Error detected: " + error))                                
  }, []);
  */

  return (
    <View style={styles.bg}>
      <Text style={styles.text}>Near me</Text >

      <View style={[styles.background, {marginBottom:20} ]}>
        <Text style={styles.iconstyle}>Username</Text>
        <TextInput style={styles.inputstyle}
          placeholder="Username."
          placeholderTextColor="#003f5c"
          onChangeText={(username) => setUsername(username)}/>
      </View>

      <View style={styles.background}>
        <Text style={styles.iconstyle}>Email</Text>
        <TextInput style={styles.inputstyle}
          placeholder="Email."
          placeholderTextColor="#003f5c"
          onChangeText={(email) => setEmail(email)}/>
      </View>

      <TouchableOpacity onPress={() => navigation.navigate('Sign')}>
        <Text style={styles.asktext}>Don't have an account? sign in with us here!</Text>
      </TouchableOpacity>

      <View style={styles.background}>
        <Text style={styles.iconstyle}>Password</Text>
        <TextInput style={styles.inputstyle}
          placeholder="Password."
          placeholderTextColor="#003f5c"
          secureTextEntry={true}
          onChangeText={(password) => setPassword(password)}/>
      </View>

      <TouchableOpacity>
        <Text style={styles.asktext}>Forgot password</Text>
      </TouchableOpacity>

      <PrimaryButton
        //onPress={() => {navigation.navigate('Canteen'); console.log(password)}}
        onPress={() => {
          if (email.trim() === "" || password.trim() === "" || username.trim() === "") {
          } 
          else {
            //{navigation.navigate('Canteen'); console.log(email)};
            {apicall(email, password, username)};
          }
        }}
        title="Login"
      />
    </View>
  );
};

const styles = StyleSheet.create({
  text: {
    fontSize: 30,
    fontWeight: 'bold',
    paddingBottom: 20,
    paddingTop: 20,
    alignSelf: 'center'
  },
  input: {
      margin: 15,
      borderColor: 'black',
      borderWidth: 1
  },
  bg: {
    backgroundColor: 'white',
    flexDirection: 'column'
  },
  asktext: {
    fontSize: 10,
    color: 'orange',
    paddingBottom: 20,
    paddingLeft: 20
  },
  background: {
    backgroundColor: '#F0EEEE',
    borderRadius: 10,
    marginHorizontal: 15,
    height: 50,
    flexDirection: 'column',
    marginTop: 10
  },
  inputstyle: {
    fontSize: 14,
    paddingLeft: 20
  },
  iconstyle:{
    fontSize: 14,
    marginHorizontal: 10,
    fontWeight: 'bold'
  }
});

export default LoginScreen;


